package br.gov.sp.educacao.minhaescola.adapter;import android.app.Activity;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.TextView;import java.util.List;import br.gov.sp.educacao.minhaescola.R;import br.gov.sp.educacao.minhaescola.model.Frequencia;import butterknife.ButterKnife;import butterknife.BindView;public class FrequenciaAdapter        extends BaseAdapter {    private final List<Frequencia> bimestreFrequencias;    private final Activity act;    public @BindView(R.id.item_frequencia_disciplina) TextView txtDisciplina;    public @BindView(R.id.item_frequencia_faltas) TextView txtFaltas;    public @BindView(R.id.item_frequencia_ausencias) TextView txtAusencias;    public @BindView(R.id.item_frequencia_porcentagem) TextView txtPorcentagem;    public FrequenciaAdapter(List<Frequencia> bimestreFrequencias, Activity act) {        this.bimestreFrequencias = bimestreFrequencias;        this.act = act;    }    @Override    public int getCount() {        return bimestreFrequencias.size();    }    @Override    public Object getItem(int position) {        return bimestreFrequencias.get(position);    }    @Override    public long getItemId(int position) {        return 0;    }    @Override    public View getView(int position, View view, ViewGroup viewGroup) {        View v = act.getLayoutInflater().inflate(R.layout.item_frequencia, viewGroup, false );        Frequencia frequencia = bimestreFrequencias.get(position);        ButterKnife.bind(this, v);        txtDisciplina.setText(frequencia.getNome_disciplina());        if (String.valueOf(frequencia.getFalta()).equals("-1")                && String.valueOf(frequencia.getAusencia_compensada()).equals("-1")) {            txtFaltas.setText("-");            txtAusencias.setText("-");        }        else {            txtFaltas.setText(String.valueOf(frequencia.getFalta()));            txtAusencias.setText(String.valueOf(frequencia.getAusencia_compensada()));        }        float porcentagemFrequencia = 100f - frequencia.getPorcentagemFalta();        txtPorcentagem.setText(String.valueOf(Math.round(porcentagemFrequencia)) + "%");        return v;    }}