package br.gov.sp.educacao.minhaescola.model;import android.os.Parcel;import android.os.Parcelable;public class DiasEventos        implements Parcelable {    private String bimestre;    private String dia;    private String mes;    private boolean letivo;    private String nomeEvento;    private String descricaoEvento;    private String nomeDisciplina;    public DiasEventos (String bimestre,                        String mes,                        String dia,                        boolean letivo,                        String nomeEvento,                        String descricaoEvento,                        String nomeDisciplina) {        this.bimestre = bimestre;        this.mes = mes;        this.dia = dia;        this.letivo = letivo;        this.nomeEvento = nomeEvento;        this.descricaoEvento = descricaoEvento;        this.nomeDisciplina = nomeDisciplina;    }    public DiasEventos () {}    private DiasEventos(Parcel in) {        bimestre = in.readString();        mes = in.readString();        dia = in.readString();        letivo = in.readByte() != 0;        nomeEvento = in.readString();        descricaoEvento = in.readString();        nomeDisciplina = in.readString();    }    public String getBimestre() {        return bimestre;    }    public void setBimestre(String bimestre) {        this.bimestre = bimestre;    }    public String getMes() {        return mes;    }    public void setMes(String mes) {        this.mes = mes;    }    public String getNomeDisciplina() {        return nomeDisciplina;    }    public void setNomeDisciplina(String nomeDisciplina) {        this.nomeDisciplina = nomeDisciplina;    }    public void setDia(String dia) {        this.dia = dia;    }    public void setLetivo(boolean letivo) {        this.letivo = letivo;    }    public void setNomeEvento(String nomeEvento) {        this.nomeEvento = nomeEvento;    }    public void setDescricaoEvento(String descricaoEvento) {        this.descricaoEvento = descricaoEvento;    }    public String getDia() {        return dia;    }    public boolean isLetivo() {        return letivo;    }    public String getNomeEvento() {        return nomeEvento;    }    public String getDescricaoEvento() {        return descricaoEvento;    }    @Override    public int describeContents() {        return 0;    }    @Override    public void writeToParcel (Parcel dest, int flags) {        dest.writeString(bimestre);        dest.writeString(mes);        dest.writeString(dia);        dest.writeByte((byte) (letivo ? 1 : 0));        dest.writeString(nomeEvento);        dest.writeString(descricaoEvento);        dest.writeString(nomeDisciplina);    }    public static final Parcelable.Creator<DiasEventos> CREATOR = new Parcelable.Creator<DiasEventos>() {        @Override        public DiasEventos createFromParcel (Parcel in) {            return new DiasEventos(in);        }        @Override        public DiasEventos[] newArray(int size) {            return new DiasEventos[size];        }    };}