package br.gov.sp.educacao.minhaescola.modelTO;import org.json.JSONArray;import org.json.JSONException;import org.json.JSONObject;import java.util.ArrayList;import br.gov.sp.educacao.minhaescola.model.Frequencia;public class FrequenciasTO {    private JSONArray jsonFreq;    private int cd_aluno;    public FrequenciasTO(JSONArray jsonFreq, int cd_aluno) {        this.jsonFreq = jsonFreq;        this.cd_aluno = cd_aluno;    }    public ArrayList<Frequencia> getFrequenciaFromJson() {        ArrayList<Frequencia> frequenciasNota = new ArrayList<Frequencia>();        try {            int tamanho = jsonFreq.length();            for (int b = 0; b < tamanho; b++) {                JSONObject jsonFregs = jsonFreq.getJSONObject(b);                JSONArray freqArray = jsonFregs.getJSONArray("Notas");                Frequencia frequencia;                for (int n = 0; n < freqArray.length(); n++) {                    frequencia = new Frequencia();                    frequencia.setCd_aluno(cd_aluno);                    frequencia.setCd_disciplina(freqArray.getJSONObject(n).getInt("CodigoDisciplina"));                    frequencia.setNome_disciplina(freqArray.getJSONObject(n).getString("NomeDisciplina"));                    frequencia.setFalta(freqArray.getJSONObject(n).getInt("Faltas"));                    frequencia.setBimestre(jsonFregs.getInt("Bimestre"));                    frequencia.setAusencias_compensada(freqArray.getJSONObject(n).getInt("AusenciasCompensadas"));                    frequencia.setPorcentagemfalta(freqArray.getJSONObject(n).getInt("PorcentagemFaltas"));                    frequenciasNota.add(frequencia);                }            }            return frequenciasNota;        }        catch (JSONException e) {            e.printStackTrace();            return null;        }    }}